         ListItem(title: 'checkbox_demo',pageWidget: CheckBoxDemo(),),          ListItem(title: 'ChipDemo',pageWidget: ChipDemo(),),          ListItem(title: 'ContainerDemo',pageWidget: ContainerDemo(),),          ListItem(title: 'DataTableDemo',pageWidget: DataTableDemo(),),          ListItem(title: 'DataTimeDemo',pageWidget: DataTimeDemo(),),          ListItem(title: 'DrawerDemo',pageWidget: DrawerDemo(),),          ListItem(title: 'ExploreDemo',pageWidget: ExploreDemo(),),          ListItem(title: 'FromDemo',pageWidget: FromDemo(),),          ListItem(title: 'Hello',pageWidget: Hello(),),          ListItem(title: 'LayoutDemo',pageWidget: LayoutDemo(),),          ListItem(title: 'ListViewDemo',pageWidget: ListViewDemo(),),          ListItem(title: 'MaterialComponents',pageWidget: MaterialComponents(),),          ListItem(title: 'MineNews',pageWidget: MineNews(),),          ListItem(title: 'NavagatorDamo',pageWidget: NavagatorDamo(),),          ListItem(title: 'PopupMenuButtonDemo',pageWidget: PopupMenuButtonDemo(),),          //ListItem(title: 'PostShow',pageWidget: PostShow(),),          ListItem(title: 'SimpleDialogDemo',pageWidget: SimpleDialogDemo(),),          ListItem(title: 'SliverDemo',pageWidget: SliverDemo(),),          ListItem(title: 'SnackBarDemo',pageWidget: SnackBarDemo(),),          ListItem(title: 'ViewDemo',pageWidget: ViewDemo(),),        ],      ),    );  }}class ListItem extends StatelessWidget{  final String title;  final Widget pageWidget;  //初始化属性  ListItem({    this.pageWidget,    this.title,  });  @override  Widget build(BuildContext context) {    return ListTile(      title: Text(title),      onTap: (){        Navigator.of(context).push(          //创建 一个页面 把页面压到navigator的stack中          MaterialPageRoute(builder: (context) => pageWidget),        );      },    );  }}